import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars } from '@fortawesome/free-solid-svg-icons';
import './navbar.css';

function Navbar() {
  const [sidebarOpen, setSidebarOpen] = useState(false); // State for navbar1 sidebar
  const [navbar2Open, setNavbar2Open] = useState(false); // State for navbar2 sliding

  // Toggle for navbar1 (sidebar)
  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  // Automatically open navbar2 on mobile (for sliding in)
  React.useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth <= 768) {
        setNavbar2Open(true);
      } else {
        setNavbar2Open(false);
      }
    };
    
    window.addEventListener('resize', handleResize);
    handleResize(); // Initial call to set the state based on screen size
    
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <nav className="navbar">
      {/* Header Section (Logo and Hamburger menu for mobile) */}
      <div className="navbar-header">
        <div className="navbar-logo">
          <h1>Business Solution</h1>
        </div>
        <div className="navbar-hamburger" onClick={toggleSidebar}>
          <FontAwesomeIcon icon={faBars} />
        </div>
        {/* Navbar2 (Next to logo on larger screens) */}
        <div className={`navbar2-desktop ${navbar2Open ? 'slide-in' : ''}`}>
          <ul className="navbar-menu2">
            <li>Products</li>
            <li>Solutions</li>
            <li>Customers</li>
            <li>Resources</li>
            <li>Contact us</li>
            <li>Get started</li>
          </ul>
        </div>
      </div>

      {/* Sidebar for navbar1 (mobile view) */}
      <div className={`sidebar ${sidebarOpen ? 'open' : ''}`}>
        <ul className="sidebar-menu">
          <li>Driver</li>
          <li>Rider</li>
          <li>Business</li>
          <li>Log in</li>
          <li>Sign up</li>
        </ul>
      </div>
    </nav>
  );
}

export default Navbar;
