import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars, faGlobe } from '@fortawesome/free-solid-svg-icons';
import './navbar.css';

function Navbar() {
  // State to manage which navbar to show
  const [navbarState, setNavbarState] = useState(0); // 0: closed, 1: navbar1

  // Function to handle clicks on the hamburger icon
  const toggleNavbar = () => {
    setNavbarState((prev) => (prev + 1) % 2); // Cycle through 0, 1
  };

  return (
    <nav className="navbar">
      {/* Header Section (Logo and Hamburger menu for mobile) */}
      <div className="navbar-header">
        <div className="navbar-logo">
          <h1>Business Solution</h1>
        </div>
        <div className="navbar-hamburger" onClick={toggleNavbar}>
          <FontAwesomeIcon icon={faBars} />
        </div>
      </div>

      {/* Display Navbar1 on larger screens */}
      <div className="navbar1-desktop">
        <ul className="navbar-menu">
          <li>Driver</li>
          <li>Rider</li>
          <li>Business</li>
          <li>Log in</li>
          <li>Sign up</li>
          <li><FontAwesomeIcon icon={faGlobe} /> En</li>
        </ul>
      </div>

      {/* Sidebar for mobile view */}
      <div className={`sidebar ${navbarState > 0 ? 'open' : ''}`}>
        {navbarState === 1 && ( // Show navbar1 if state is 1
          <ul className="sidebar-menu">
            <li>Driver</li>
            <li>Rider</li>
            <li>Business</li>
            <li>Log in</li>
            <li>Sign up</li>
            <li><FontAwesomeIcon icon={faGlobe} /> En</li>
          </ul>
        )}
      </div>
    </nav>
  );
}

export default Navbar;
